import React from 'react';
import PostButton from './UI/button/PostButton';

const Post = function(props) {

  return (
    <div className='post'>
      <div className='post__content'>
      <div className='post__id'>{props.number}</div>
      <strong>{props.info.head}</strong>
      <div>{props.info.text}</div>
      </div>
      <div className='post__button'>
      <PostButton onClick = {() => props.remove(props.info)}>Удалить</PostButton>
      </div>    
    </div>
  )

}

export default Post
