import { useMemo } from "react";

export const useSortedPost = (sort, posts) => {
  useMemo(() => {
  if (sort) {
    return [...posts].sort((a,b) => a[sort].localeCompare(b[sort]))
  } 
  return posts;
}, [sort, posts]); 
}

export const usePosts = (posts, sort, search) => {
  const sortedPosts = useSortedPost(sort, posts);
  const searchPosts = useMemo(
    () => {
      return sortedPosts.filter(post => post.head.toLocaleLowerCase().includes(search))
    },
    [search, sortedPosts]
  )

  return searchPosts
}
