import React, {useState} from 'react';
import PostInput from './UI/input/PostInput';
import PostButton from './UI/button/PostButton';


const PostForm = ({create}) => {
  const [post, setPost] = useState({head: ' ', text: ' '});

  const addNewPost = (e) => {
    e.preventDefault();

    const newPost = {
      ...post, id: Date.now()
    }
    create(newPost)
    setPost({head: ' ', text: ' '})
  
   };

  return (
      <form>
      <PostInput type ="text" placeholder = "Имя поста" value = {post.head} onChange ={e => setPost({...post, head: e.target.value})}></PostInput>
      <PostInput  type = 'text' placeholder= 'Текст поста' value = {post.text} onChange ={e => setPost({...post, text: e.target.value})} />
      <PostButton onClick= {addNewPost}>Опубликовать</PostButton>
      </form>   
  );
};

export default PostForm;
