import React, {useState, useMemo} from 'react';
import Post from './components/post.jsx'
import PostList from './components/postlist.jsx';
import './style/style.css'
import PostButton from './components/UI/button/PostButton.jsx';
import PostInput from './components/UI/input/PostInput.jsx';
import PostForm from './components/postform.jsx';
import PostSelect from './components/UI/select/postSelect.jsx';

function App() {
 const [posts, postsState] = useState(
 [ {id: 1, head: "A", text: "CCCC Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur." },
  {id: 2, head: "C", text: "AAAA Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur." },
  {id: 3, head: "B", text: "BBBB Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur." }]
 );

 const [selected, setSelected] = useState('');
 const [searchQuery, setSearchQuety] = useState('');



const sortedPosts =  useMemo(() => {
  console.log('worked')
  if (selected) {
    return [...posts].sort((a,b) => a[selected].localeCompare(b[selected]))
  } 
  return posts;
}, [selected, posts]); 

const searchPosts = useMemo(
  () => {
    return sortedPosts.filter(post => post.head.toLocaleLowerCase().includes(searchQuery))
  },
  [searchQuery, sortedPosts]
)


 const sortPost = (sort) => {
    setSelected(sort)
 };

 const createPost = (newPost) => {
  postsState([...posts, newPost])
 };

 const removePost = (post) => {
  postsState(posts.filter(p => p.id !== post.id))
 };

  return (
    <div className="App">
      <PostForm create = {createPost}/>
      <hr style={{margin: '15px 0'}}/>
      <div> 
        <PostInput 
        placeholder = "Поиск"
        value = {searchQuery}
        onChange = {e => setSearchQuety(e.target.value)}
        />
        <PostSelect 
        value={selected}
        onChange = {sortPost}
        defaultValue = "Сортировка по" 
        options = {[
          {value: "head", text: "По названию"},
          {value: "text", text: "По описанию"},
          ]} 
          />
      </div>
      {posts.length 
      ? <PostList remove = {removePost} posts={searchPosts} title = 'Список постов 1' /> 
      : <div className='postEmpty'>Список постов пуст</div> }
    </div>
  );
}

export default App;
