import React from 'react';

const postSelect = ({options, defaultValue}) => {
  return (
    <select>
      <option value="">{defaultValue}</option>
      {options.map(option => 
        <option value={option.value}>{option.text}</option>
      )}
    </select>
  );
};

export default postSelect;
