import React, {useState} from 'react';
import Post from './components/post.jsx'
import PostList from './components/postlist.jsx';
import './style/style.css'
import PostButton from './components/UI/button/PostButton.jsx';
import PostInput from './components/UI/input/PostInput.jsx';

function App() {
 const [posts, postsState] = useState(
 [ {id: 1, head: "Post 1", text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur." },
  {id: 2, head: "Post 2", text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur." },
  {id: 3, head: "Post 3", text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur." }]
 );

 const [post, setPost] = useState({head: ' ', text: ' '});

 const addNewPost = (e) => {
  e.preventDefault();
  postsState([...posts, {post, id:Date.now() }]);
  setPost({head: ' ', text: ' '})

 };

  return (
    <div className="App">
      <form>
      <PostInput type ="text" placeholder = "Имя поста" value = {post.head} onChange ={e => setPost({...post, head: e.target.value})}></PostInput>
      <PostInput  type = 'text' placeholder= 'Текст поста' value = {text} onChange ={e => setPost({...post, text: e.target.value})} />
      <PostButton onClick= {addNewPost}>Опубликовать</PostButton>
      </form>   
      <PostList posts={posts} title = 'Список постов 1' />

    </div>
  );
}

export default App;
